commits.content:
@tailwind base;
@tailwind components;
@tailwind utilities;
@tailwind variants;

.custom-dialog-container .mat-dialog-container {
  @apply rounded-xl shadow-2xl;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000; /* Increase the z-index to ensure the dialog appears on top */
}

.cdk-overlay-container{
  position: absolute;
  top: 20%;
  left: 30%;
  z-index: 1000;
}

/* Global styles for snackbar */
.success-snackbar {
  background-color: #4CAF50;
  color: white;
}

.error-snackbar {
  background-color: #F44336;
  color: white;
}

.warning-snackbar {
  background-color: #FF9800;
  color: white;

}

::ng-deep .custom-merchant-select .ng-dropdown-panel .ng-dropdown-panel-items .ng-option {
  background-color: #f4f4f4;
  color: #333;
  padding: 10px;
  transition: all 0.2s ease;
}

::ng-deep .custom-merchant-select .ng-dropdown-panel .ng-dropdown-panel-items .ng-option:hover {
  background-color: #e0e0e0;
}

::ng-deep .custom-merchant-select .ng-dropdown-panel .ng-dropdown-panel-items .ng-option.ng-option-selected {
  background-color: #007bff;
  color: white;
}
